import i18n from 'i18next';
import { initReactI18next } from 'react-i18next';

i18n.use(initReactI18next).init({
  resources: {
    en: {
      translation: {
        welcome: "Welcome to AgriConnect",
        login: "Login",
        signup: "Sign Up"
      }
    },
    fr: {
      translation: {
        welcome: "Bienvenue à AgriConnect",
        login: "Connexion",
        signup: "S'inscrire"
      }
    },
    rw: {
      translation: {
        welcome: "Murakaza neza kuri AgriConnect",
        login: "Injira",
        signup: "Iyandikishe"
      }
    }
  },
  lng: "en",
  fallbackLng: "en",
  interpolation: {
    escapeValue: false
  }
});

export default i18n;
